package main

import (
	"encoding/hex"
	"github.com/herumi/bls-eth-go-binary/bls"
)

var sk = func() *bls.SecretKey {
	_ = bls.Init(bls.BLS12_381)
	_ = bls.SetETHmode(bls.EthModeDraft07)

	str := "6c71a538e1ee50160a09d6e43e1a29bcc6e01c4af5be6dc553d383c5aceaa8aa"
	ret := &bls.SecretKey{}
	if err := ret.DeserializeHexStr(str); err != nil {
		panic(err.Error())
	}
	return ret
}()

var pk = sk.GetPublicKey()

